import React from "react";

const ChatBar = (props) => {
  const [state, setState] = React.useState([
    { id: 1, image: "/images/pic1.jpg", name: "Annette Mariana" },
    { id: 2, image: "/images/pic2.jpg", name: "Aranguren Diannys" },
    { id: 3, image: "/images/pic3.jpg", name: "Michelle Kat" },
    { id: 4, image: "/images/pic4.jpg", name: "Valen Mascareño" },
    { id: 5, image: "/images/pic5.jpg", name: "Alanis querales" },
    { id: 6, image: "/images/pic6.jpg", name: "Nicoll Vargas" },
    { id: 6, image: "/images/pic7.jpg", name: "Yeni Peraza" },
    { id: 7, image: "/images/profile.jpg", name: "Maria Velazquez" },
    { id: 8, image: "/images/fawad1.jpg", name: "Jesus Manzanarez" },
    { id: 9, image: "/images/fawad2.jpg", name: "Luisanyelis Camacaro" },
    { id: 10, image: "/images/fawad3.jpg", name: "Olianny Yepez" },
    { id: 10, image: "/images/fawad3.jpg", name: "Olianny Yepez" },
    { id: 10, image: "/images/fawad3.jpg", name: "Olianny Yepez" },
    { id: 10, image: "/images/fawad3.jpg", name: "Olianny Yepez" },
    { id: 10, image: "/images/fawad3.jpg", name: "Olianny Yepez" },
    { id: 10, image: "/images/fawad3.jpg", name: "Olianny Yepez" },
    { id: 10, image: "/images/fawad3.jpg", name: "Olianny Yepez" },
  ]);
  const openChat = (user) => {
    props.openChat(user);
  };
  return (
    <div className="chatBar">
      {state.map((user) => (
        <div
          className="chatBar__list"
          key={user.id}
          onClick={() => openChat(user)}
        >
          <div className="chatBar__list-img">
            <img src={user.image} alt="user" />
            <span className="status"></span>
          </div>
          <div className="chatBar__list-name">{user.name}</div>
        </div>
      ))}
    </div>
  );
};

export default ChatBar;
